import { createRouter, createWebHashHistory } from 'vue-router'

import BookmarksView from "@/views/BookmarksView.vue";
import ExploreView from "@/views/ExploreView.vue";
import LoginView from "@/views/template/LoginView.vue";
import PostView from "@/views/PostView.vue";
import ProfileView from "@/views/ProfileView.vue";
import ContentView from "@/views/ContentView.vue";
import CommentsView from "@/views/CommentsView.vue";
import SignupView from "@/views/template/SignupView.vue";
import TemplateView from "@/views/template/TemplateView.vue";

const routes = [

    [{
    path: '/',
    redirect: '/login',
    children:
      {
        path: "signup",
        component: () => import("@/views/template/SignupView.vue"),
      },

  }],

  [{
    path: '/dashboard',
    name: 'Dashboard',
    component: TemplateView,
    meta: { requiresAuth: true },
  },
  {
    path: '/content',
    name: 'content',
    component: ContentView
  },
  {
    path: '/bookmarks',
    name: 'bookmarks',
    component: BookmarksView
  },
  {
    path: '/explore',
    name: 'explore',
    component: ExploreView
  },
  {
    path: '/post',
    name: 'post',
    component: PostView
  },
  {
    path: '/profile',
    name: 'profile',
    component: ProfileView
  },
  {
    path: '/comments',
    name: 'Comments',
    component: CommentsView
  }]
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
});

export default router;

